    "forms/select": "<div class=\"form-group {{#if error}}has-error has-feedback{{/if}}\">\n  <label for=\"{{fieldId}}\">{{{label}}} {{#ifCond field.allowNull false}}*{{/ifCond}}</label>\n  <select class=\"form-control\" id=\"{{fieldId}}\" name=\"{{name}}\">\n    {{#if field.allowNull}}\n      <option value=\"\" selected=\"selected\">{{{placeholder}}}</option>\n    {{/if}}\n    {{#each field.fieldOptions}}\n      {{#ifCond @key ../value}}\n        {{#with ../this}}\n          <option selected=\"selected\" value=\"{{@key}}\">{{t this}}</option>\n        {{/with}}\n      {{else}}\n        {{#with ../this}}\n          <option value=\"{{@key}}\">{{t this}}</option>\n        {{/with}}\n      {{/ifCond}}\n    {{/each}}\n  </select>\n  <p class=\"help-block\">{{t help}}</p>\n  {{{field-help-error error}}}\n</div>",
