    "flag/like": "{{#if locals.req.user}}\n  {{#if isFlagged}}\n  <form class=\"unlike-form\" action=\"/api/v1/unflag/{{modelName}}/{{modelId}}?flagType=like\" method=\"post\" data-we-modelName=\"{{modelName}}\" data-we-modelId=\"{{modelId}}\">\n    <input name=\"redirectTo\" type=\"hidden\" value=\"{{redirectTo}}\">\n\n    <div class=\"input-group input-group-sm\">\n      <span class=\"input-group-addon\">{{count}}</span>\n      <button class=\"btn btn-sm btn-default\">{{t 'flag.unlike'}}</button>\n    </div>\n\n  </form>\n  {{else}}\n  <form class=\"like-form\" action=\"/api/v1/flag/{{modelName}}/{{modelId}}?flagType=like\" method=\"post\" data-we-modelName=\"{{modelName}}\" data-we-modelId=\"{{modelId}}\">\n    <input name=\"redirectTo\" type=\"hidden\" value=\"{{redirectTo}}\">\n\n    <div class=\"input-group input-group-sm\">\n      <span class=\"input-group-addon\">{{count}}</span>\n      <button class=\"btn btn-sm btn-default\">{{t 'flag.like'}}</button>\n    </div>\n  </form>\n  {{/if}}\n{{else}}\n  <form>\n    <div class=\"input-group input-group-sm disabled\">\n      <span class=\"input-group-addon\">{{count}}</span>\n      <button disabled class=\"disabled btn btn-sm btn-default\">{{t 'flag.like'}}</button>\n    </div>\n  </form>\n{{/if}}",
